css:
/* Reset and Full Width */
html, body, .print-format, .container, .page-break {
    width: 100% !important;
    max-width: none !important;
    padding: 0 !important;
    margin: 0 !important;
    background-color: white;
}

@page {
    size: A4 portrait;
    margin: 0.5in;
}

/* Modern Typography */
.print-format {
    font-family: 'Inter', system-ui, -apple-system, sans-serif;
    color: #111827;
    font-size: 13px;
    line-height: 1.5;
    box-sizing: border-box;
}

/* Header */
.header-section {
    display: flex;
    justify-content: space-between;
    align-items: flex-start;
    padding-bottom: 20px;
    border-bottom: 3px solid #ef4444;
    margin-bottom: 30px;
}

.company-branding h1 {
    margin: 0;
    font-size: 24px;
    color: #ef4444;
    font-weight: 800;
    letter-spacing: -0.01em;
    text-transform: uppercase;
}

.company-branding p {
    margin: 4px 0 0;
    color: #6b7280;
    font-size: 13px;
    font-weight: 500;
}

.doc-info {
    text-align: right;
}

.doc-title {
    font-size: 20px;
    font-weight: 800;
    text-transform: uppercase;
    color: #1f2937;
    margin: 0;
}

.doc-number {
    font-size: 14px;
    font-weight: 600;
    color: #374151;
    margin-top: 4px;
    font-family: monospace;
}

/* Info Grid */
.info-grid {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 40px;
    margin-bottom: 30px;
}

.info-group {
    margin-bottom: 15px;
}

.info-label {
    font-size: 11px;
    font-weight: 700;
    text-transform: uppercase;
    color: #9ca3af;
    margin-bottom: 4px;
    letter-spacing: 0.05em;
}

.info-value {
    font-size: 14px;
    font-weight: 500;
    color: #111827;
}

/* Table */
.table-modern {
    width: 100%;
    border-collapse: collapse;
    margin-bottom: 30px;
    font-size: 13px;
}

.table-modern th {
    text-align: left;
    padding: 10px 12px;
    background-color: #f3f4f6;
    color: #374151;
    font-weight: 700;
    text-transform: uppercase;
    font-size: 11px;
    border-bottom: 2px solid #e5e7eb;
    letter-spacing: 0.05em;
}

.table-modern td {
    padding: 10px 12px;
    border-bottom: 1px solid #f3f4f6;
    color: #1f2937;
    vertical-align: top;
}

.table-modern tr:last-child td {
    border-bottom: 2px solid #ef4444;
}

.badge {
    background: #eef2ff;
    color: #4338ca;
    padding: 2px 6px;
    border-radius: 4px;
    font-family: monospace;
    font-weight: 600;
    font-size: 12px;
}

/* Totals */
.totals-section {
    display: flex;
    justify-content: flex-end;
}

.totals-box {
    width: 45%;
    padding-top: 10px;
}

.total-row {
    display: flex;
    justify-content: space-between;
    padding: 6px 0;
    font-size: 14px;
    color: #4b5563;
}

.total-row.final {
    font-weight: 800;
    font-size: 18px;
    color: #ef4444;
    border-top: 1px solid #fee2e2;
    margin-top: 10px;
    padding-top: 10px;
}

/* Footer */
.footer-section {
    margin-top: 50px;
    padding-top: 20px;
    border-top: 1px solid #f3f4f6;
    text-align: center;
    font-size: 12px;
    color: #9ca3af;
}
