css:
/* Reset and Full Width */
html, body, .print-format, .container, .page-break {
    width: 100% !important;
    max-width: none !important;
    padding: 0 !important;
    margin: 0 !important;
    background-color: white;
}

@page {
    size: A4 portrait;
    margin: 0.5in;
}

/* Modern Typography */
.print-format {
    font-family: 'Inter', system-ui, -apple-system, sans-serif;
    color: #111827;
    font-size: 13px;
    line-height: 1.5;
    box-sizing: border-box;
}

/* Header */
.header-section {
    display: flex;
    justify-content: space-between;
    align-items: flex-start;
    padding-bottom: 20px;
    border-bottom: 3px solid #10b981;
    margin-bottom: 30px;
}

.company-branding h1 {
    margin: 0;
    font-size: 24px;
    color: #10b981;
    font-weight: 800;
    letter-spacing: -0.01em;
    text-transform: uppercase;
}

.company-branding p {
    margin: 4px 0 0;
    color: #6b7280;
    font-size: 13px;
    font-weight: 500;
}

.doc-info {
    text-align: right;
}

.doc-title {
    font-size: 20px;
    font-weight: 800;
    text-transform: uppercase;
    color: #1f2937;
    margin: 0;
}

.doc-number {
    font-size: 14px;
    font-weight: 600;
    color: #374151;
    margin-top: 4px;
    font-family: monospace;
}

/* Info Grid */
.info-grid {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 40px;
    margin-bottom: 30px;
}

.info-group {
    margin-bottom: 15px;
}

.info-label {
    font-size: 11px;
    font-weight: 700;
    text-transform: uppercase;
    color: #9ca3af;
    margin-bottom: 4px;
    letter-spacing: 0.05em;
}

.info-value {
    font-size: 14px;
    font-weight: 500;
    color: #111827;
}

/* Table */
.table-modern {
    width: 100%;
    border-collapse: collapse;
    margin-bottom: 30px;
    font-size: 13px;
}

.table-modern th {
    text-align: left;
    padding: 10px 12px;
    background-color: #f3f4f6;
    color: #374151;
    font-weight: 700;
    text-transform: uppercase;
    font-size: 11px;
    border-bottom: 2px solid #e5e7eb;
    letter-spacing: 0.05em;
}

.table-modern td {
    padding: 10px 12px;
    border-bottom: 1px solid #f3f4f6;
    color: #1f2937;
    vertical-align: top;
}

.table-modern tr:last-child td {
    border-bottom: 2px solid #10b981;
}

.badge {
    background: #ecfdf5;
    color: #047857;
    padding: 2px 6px;
    border-radius: 4px;
    font-family: monospace;
    font-weight: 600;
    font-size: 12px;
}

/* Footer */
.footer-section {
    margin-top: 40px;
    padding-top: 20px;
    display: flex;
    justify-content: space-between;
    align-items: flex-end;
}

.terms {
    font-size: 11px;
    color: #6b7280;
    max-width: 65%;
    line-height: 1.4;
}

.qr-box {
    border: 1px solid #e5e7eb;
    padding: 4px;
    border-radius: 4px;
    background: white;
}

.qr-box img {
    width: 80px;
    height: 80px;
    display: block;
}
